import { ValidationContext, SDLValidationContext } from 'graphql-upload/node_modules/graphql/validation/ValidationContext';
import { ASTVisitor } from 'graphql-upload/node_modules/graphql/language/visitor';

export function unknownArgMessage(
  argName: string,
  fieldName: string,
  typeName: string,
  suggestedArgs: ReadonlyArray<string>,
): string;

export function unknownDirectiveArgMessage(
  argName: string,
  directiveName: string,
  suggestedArgs: ReadonlyArray<string>,
): string;

/**
 * Known argument names
 *
 * A GraphQL field is only valid if all supplied arguments are defined by
 * that field.
 */
export function KnownArgumentNames(context: ValidationContext): ASTVisitor;

// @internal
export function KnownArgumentNamesOnDirectives(
  context: ValidationContext | SDLValidationContext,
): ASTVisitor;
