import { ASTVisitor } from 'graphql-upload/node_modules/graphql/language/visitor';
import { ValidationContext, SDLValidationContext } from 'graphql-upload/node_modules/graphql/validation/ValidationContext';

export function unknownDirectiveMessage(directiveName: string): string;

export function misplacedDirectiveMessage(
  directiveName: string,
  location: string,
): string;

/**
 * Known directives
 *
 * A GraphQL document is only valid if all `@directives` are known by the
 * schema and legally positioned.
 */
export function KnownDirectives(
  context: ValidationContext | SDLValidationContext,
): ASTVisitor;
